create table tiket(id_tiket number(10), nama_tiket varchar(20), id_kelas number(10), harga_tiket number(20), tujuan varchar(20));
create table jadwal(id_jadwal number(10), tujuan varchar(20), jam varchar(10));
create table customer(id_kustomer number(10), password varchar(10), nama varchar(15), alamat varchar(15), email varchar(15));